mapper.select:
SELECT id, player_id, warship_id, state_id
FROM player_warship
WHERE id = #{id}

SELECT id, player_id, warship_id, state_id
FROM player_warship
WHERE player_id = #{playerId}
  AND warship_id = #{warshipId}

mapper.update:
UPDATE player_warship
SET player_id = #{playerId},
    warship_id = #{warshipId},
    state_id = #{stateId}
WHERE id = #{id}

mapper.delete:
DELETE FROM player_warship
WHERE player_id = #{playerId}
  AND warship_id = #{warshipId}
