mapper.select:
SELECT id, state_type_id, state_type_name, weapon_types
FROM state_type
WHERE id = #{id}

SELECT id, state_type_id, state_type_name, weapon_types
FROM state_type
WHERE state_type_id = #{stateTypeId}

mapper.update:
UPDATE state_type
SET state_type_id = #{stateTypeId},
    state_type_name = #{stateTypeName},
    weapon_types = #{weaponTypes}
WHERE id = #{id}
